(1, 'reputation_list.html', '', 1301147984, '<!-- INCLUDE overall_header.html -->\n<h2 class="solo">{PAGE_TITLE}</h2>\n\n<div class="panel">\n	<div class="inner"><span class="corners-top"><span></span></span>\n		<ul class="linklist">\n			<li class="rightside pagination">\n				{TOTAL_USERS} &bull; \n				<!-- IF PAGINATION -->\n          <a href="#" onclick="jumpto(); return false;" title="{L_JUMP_TO_PAGE}">{PAGE_NUMBER}</a> &bull; <span>{PAGINATION}</span>\n        <!-- ELSE -->\n          {PAGE_NUMBER}\n        <!-- ENDIF -->\n			</li>\n		</ul>\n		<span class="corners-bottom"><span></span></span>\n  </div>\n</div>\n		\n<div class="forumbg forumbg-table">\n	<div class="inner">\n    <span class="corners-top"><span></span></span>\n\n		<table class="table1" cellspacing="1">\n			<thead>\n				<tr>\n					<th class="name"><span class="rank-img">{L_RANK}</span>{L_USERNAME}</th>\n					<th class="posts">{L_POSTS}</th>\n					<th class="posts">{L_REPUTATION}</th>\n					<th class="info">{L_WEBSITE}{L_COMMA_SEPARATOR}{L_LOCATION}</th>\n					<th class="joined">{L_JOINED}</th>\n					<th class="active">{L_LAST_ACTIVE}</th>\n				</tr>\n			</thead>\n			<tbody>\n  		<!-- BEGIN memberrow -->\n				<tr class="<!-- IF memberrow.S_ROW_COUNT is even -->bg1<!-- ELSE -->bg2<!-- ENDIF -->">\n					<td>\n            <!-- IF memberrow.RANK_IMG -->\n              <span class="rank-img">{memberrow.RANK_IMG}</span>\n            <!-- ELSE -->\n              <span class="rank-img">{memberrow.RANK_TITLE}</span>\n            <!-- ENDIF -->\n            {memberrow.USERNAME_FULL}\n          </td>\n          \n					<td class="posts">\n            <!-- IF memberrow.POSTS -->\n              <a href="{memberrow.U_USER_POSTS}" title="{L_SEARCH_USER_POSTS}">{memberrow.POSTS}</a>\n            <!-- ELSE -->\n              {memberrow.POSTS}\n            <!-- ENDIF -->\n          </td>\n          \n					<td class="posts">\n            <strong>\n              <a href="{memberrow.U_USER_REPUTATION}" style="color:<!-- IF memberrow.REPUTATION > 0 -->green<!-- ELSE -->red<!-- ENDIF -->">{memberrow.REPUTATION}</a>\n            </strong>\n          </td>\n          \n					<td class="info">\n            <!-- IF memberrow.U_WWW or memberrow.LOCATION -->\n              <!-- IF memberrow.U_WWW -->\n                <div>\n                  <a href="{memberrow.U_WWW}" title="{L_VISIT_WEBSITE}: {memberrow.U_WWW}">{memberrow.U_WWW}</a>\n                </div>\n              <!-- ENDIF -->\n              <!-- IF memberrow.LOCATION -->\n                <div>\n                  {memberrow.LOCATION}\n                </div>\n              <!-- ENDIF -->\n            <!-- ELSE -->\n              &nbsp;\n            <!-- ENDIF -->\n          </td>\n          \n					<td>{memberrow.JOINED}</td>\n					<td>{memberrow.VISITED}&nbsp;</td>\n				</tr>\n  		<!-- BEGINELSE -->\n  			<tr class="bg1">\n  				<td colspan="<!-- IF S_VIEWONLINE -->8<!-- ELSE -->7<!-- ENDIF -->">{L_NO_MEMBERS}</td>\n  			</tr>\n  		<!-- END memberrow -->\n			</tbody>\n		</table>\n	  \n    <span class="corners-bottom"><span></span></span>\n  </div>\n</div>\n<hr />\n\n<ul class="linklist">\n	<li class="rightside pagination">{TOTAL_USERS} &bull; \n    <!-- IF PAGINATION -->\n      <a href="#" onclick="jumpto(); return false;" title="{L_JUMP_TO_PAGE}">{PAGE_NUMBER}</a> &bull; <span>{PAGINATION}</span>\n    <!-- ELSE -->\n      {PAGE_NUMBER}\n    <!-- ENDIF -->\n  </li>\n</ul>\n\n	<!-- INCLUDE jumpbox.html -->\n	<!-- INCLUDE overall_footer.html -->'),
(1, 'viewtopic_print.html', '', 1294941660, '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\n<html xmlns="http://www.w3.org/1999/xhtml" dir="{S_CONTENT_DIRECTION}" lang="{S_USER_LANG}" xml:lang="{S_USER_LANG}">\n<head>\n\n<meta http-equiv="content-type" content="text/html; charset={S_CONTENT_ENCODING}" />\n<meta http-equiv="content-style-type" content="text/css" />\n<meta http-equiv="content-language" content="{S_USER_LANG}" />\n<meta http-equiv="imagetoolbar" content="no" />\n<meta name="resource-type" content="document" />\n<meta name="distribution" content="global" />\n<meta name="copyright" content="2000, 2002, 2005, 2007 phpBB Group" />\n<meta name="keywords" content="" />\n<meta name="description" content="" />\n<meta name="robots" content="noindex" />\n{META}\n<title>{SITENAME} &bull; {PAGE_TITLE}</title>\n\n<link href="{T_THEME_PATH}/print.css" rel="stylesheet" type="text/css" />\n</head>\n\n<body id="phpbb">\n<div id="wrap">\n	<a id="top" name="top" accesskey="t"></a>\n\n	<div id="page-header">\n		<h1>{SITENAME}</h1>\n		<p>{SITE_DESCRIPTION}<br /><a href="{U_FORUM}">{U_FORUM}</a></p>\n\n		<h2>{TOPIC_TITLE}</h2>\n		<p><a href="{U_TOPIC}">{U_TOPIC}</a></p>\n	</div>\n\n	<div id="page-body">\n		<div class="page-number">{PAGE_NUMBER}</div>\n		<!-- BEGIN postrow -->\n			<div class="post">\n				<h3>{postrow.POST_SUBJECT}</h3>\n				<div class="date">{postrow.MINI_POST_IMG}{L_POSTED}: <strong>{postrow.POST_DATE}</strong></div>\n				<div class="author">{L_POST_BY_AUTHOR} <strong>{postrow.POST_AUTHOR}</strong></div>\n				<div class="content">{postrow.MESSAGE}</div>\n			</div>\n			<hr />\n		<!-- END postrow -->\n	</div>\n\n<!--\n	We request you retain the full copyright notice below including the link to www.phpbb.com.\n	This not only gives respect to the large amount of time given freely by the developers\n	but also helps build interest, traffic and use of phpBB3. If you (honestly) cannot retain\n	the full