(1, 'viewtopic_body.html', '', 1308208620, '<!-- INCLUDE overall_header.html -->\n<!-- IF U_MCP --><p>[&nbsp;<a href="{U_MCP}">{L_MCP}</a>&nbsp;]</p><!-- ENDIF -->\n<h2><a href="{U_VIEW_TOPIC}">{TOPIC_TITLE}</a></h2>\n<!-- NOTE: remove the style="display: none" when you want to have the forum description on the topic body -->\n<!-- IF FORUM_DESC --><div style="display: none !important;">{FORUM_DESC}<br /></div><!-- ENDIF -->\n\n<!-- IF MODERATORS or U_MCP -->\n	<p>\n		<!-- IF MODERATORS -->\n			<strong><!-- IF S_SINGLE_MODERATOR -->{L_MODERATOR}<!-- ELSE -->{L_MODERATORS}<!-- ENDIF -->:</strong> {MODERATORS}\n		<!-- ENDIF -->\n\n	</p>\n<!-- ENDIF -->\n\n<!-- IF S_FORUM_RULES -->\n	<div class="rules">\n		<div class="inner"><span class="corners-top"><span></span></span>\n\n		<!-- IF U_FORUM_RULES -->\n			<a href="{U_FORUM_RULES}">{L_FORUM_RULES}</a>\n		<!-- ELSE -->\n			<strong>{L_FORUM_RULES}</strong><br />\n			{FORUM_RULES}\n		<!-- ENDIF -->\n\n		<span class="corners-bottom"><span></span></span></div>\n	</div>\n<!-- ENDIF -->\n\n<div class="topic-actions">\n\n	<div class="buttons">\n	<!-- IF not S_IS_BOT and S_DISPLAY_REPLY_INFO -->\n		<div class="<!-- IF S_IS_LOCKED -->locked-icon<!-- ELSE -->reply-icon<!-- ENDIF -->"><a href="{U_POST_REPLY_TOPIC}" title="<!-- IF S_IS_LOCKED -->{L_TOPIC_LOCKED}<!-- ELSE -->{L_POST_REPLY}<!-- ENDIF -->"><span></span><!-- IF S_IS_LOCKED -->{L_TOPIC_LOCKED_SHORT}<!-- ELSE -->{L_POST_REPLY}<!-- ENDIF --></a></div>\n	<!-- ENDIF -->\n	</div>\n\n	<!-- IF S_DISPLAY_SEARCHBOX -->\n		<div class="search-box">\n			<form method="post" id="topic-search" action="{S_SEARCHBOX_ACTION}">\n			<fieldset>\n				<input class="inputbox search tiny"  type="text" name="keywords" id="search_keywords" size="20" value="{L_SEARCH_TOPIC}" onclick="if(this.value==\'{LA_SEARCH_TOPIC}\')this.value=\'\';" onblur="if(this.value==\'\')this.value=\'{LA_SEARCH_TOPIC}\';" />\n				<input class="button2" type="submit" value="{L_SEARCH}" />\n				<input type="hidden" value="{TOPIC_ID}" name="t" />\n				<input type="hidden" value="msgonly" name="sf" />\n			</fieldset>\n			</form>\n		</div>\n	<!-- ENDIF -->\n\n	<!-- IF PAGINATION or TOTAL_POSTS -->\n		<div class="pagination">\n			<!-- IF U_VIEW_UNREAD_POST and not S_IS_BOT --><a href="{U_VIEW_UNREAD_POST}">{L_VIEW_UNREAD_POST}</a> &bull; <!-- ENDIF -->{TOTAL_POSTS}\n			<!-- IF PAGE_NUMBER --><!-- IF PAGINATION --> &bull; <a href="#" onclick="jumpto(); return false;" title="{L_JUMP_TO_PAGE}">{PAGE_NUMBER}</a> &bull; <span>{PAGINATION}</span><!-- ELSE --> &bull; {PAGE_NUMBER}<!-- ENDIF --><!-- ENDIF -->\n		</div>\n	<!-- ENDIF -->\n\n</div>\n<div class="clear"></div>\n\n<!-- IF S_HAS_POLL -->\n	<form method="post" action="{S_POLL_ACTION}">\n\n	<div class="panel">\n		<div class="inner"><span class="corners-top"><span></span></span>\n\n		<div class="content">\n			<h2>{POLL_QUESTION}</h2>\n			<p class="author">{L_POLL_LENGTH}<!-- IF S_CAN_VOTE and L_POLL_LENGTH --><br /><!-- ENDIF --><!-- IF S_CAN_VOTE -->{L_MAX_VOTES}<!-- ENDIF --></p>\n\n			<fieldset class="polls">\n			<!-- BEGIN poll_option -->\n				<dl class="<!-- IF poll_option.POLL_OPTION_VOTED -->voted<!-- ENDIF -->"<!-- IF poll_option.POLL_OPTION_VOTED --> title="{L_POLL_VOTED_OPTION}"<!-- ENDIF -->>\n					<dt><!-- IF S_CAN_VOTE --><label for="vote_{poll_option.POLL_OPTION_ID}">{poll_option.POLL_OPTION_CAPTION}</label><!-- ELSE -->{poll_option.POLL_OPTION_CAPTION}<!-- ENDIF --></dt>\n					<!-- IF S_CAN_VOTE --><dd style="width: auto;"><!-- IF S_IS_MULTI_CHOICE --><input type="checkbox" name="vote_id[]" id="vote_{poll_option.POLL_OPTION_ID}" value="{poll_option.POLL_OPTION_ID}"<!-- IF poll_option.POLL_OPTION_VOTED --> checked="checked"<!-- ENDIF --> /><!-- ELSE --><input type="radio" name="vote_id[]" id="vote_{poll_option.POLL_OPTION_ID}" value="{poll_option.POLL_OPTION_ID}"<!-- IF poll_option.POLL_OPTION_VOTED --> checked="checked"<!-- ENDIF --> /><!-- ENDIF --></dd><!-- ENDIF -->\n					<!-- IF S_DISPLAY_RESULTS --><dd class="resultbar"><div class="<!-- IF poll_option.POLL_OPTION_PCT < 20 -->pollbar1<!-- ELSEIF poll_option.POLL_OPTION_PCT < 40 -->pollbar2<!-- ELSEIF poll_option.POLL_OPTION_PCT < 60 -->pollbar3<!-- ELSEIF poll_option.POLL_OPTION_PCT < 80 -->pollbar4<!-- ELSE -->pollbar5<!-- ENDIF -->" style="width:{poll_option.POLL_OPTION_PERCENT};">{poll_option.POLL_OPTION_RESULT}</div></dd>\n					<dd><!-- IF poll_option.POLL_OPTION_RESULT == 0 -->{L_NO_VOTES}<!-- ELSE -->{poll_option.POLL_OPTION_PERCENT}<!-- ENDIF --></dd><!-- ENDIF -->\n				</dl>\n			<!-- END poll_option -->\n\n			<!-- IF S_DISPLAY_RESULTS -->\n				<dl>\n					<dt>&nbsp;</dt>\n					<dd class="resultbar">{L_TOTAL_VOTES} : {TOTAL_VOTES}</dd>\n				</dl>\n			<!-- ENDIF -->\n\n			<!-- IF S_CAN_VOTE -->\n				<dl style="border-top: none;">\n					<dt>&nbsp;</dt>\n					<dd class="resultbar"><input type="submit" name="update" value="{L_SUBMIT_VOTE}" class="button1" /></dd>\n				</dl>\n			<!-- ENDIF -->\n\n			<!-- IF not S_DISPLAY_RESULTS -->\n				<dl style="border-top: none;">\n					<dt>&nbsp;</dt>\n					<dd class="resultbar"><a href="{U_VIEW_RESULTS}">{L_VIEW_RESULTS}</a></dd>\n				</dl>\n			<!-- ENDIF -->\n			</fieldset>\n		</div>\n\n		<span class="corners-bottom"><span></span></span></div>\n		{S_FORM_TOKEN}\n		{S_HIDDEN_FIELDS}\n	</div>\n\n\n	</form>\n	<hr />\n<!-- ENDIF -->\n\n<!-- BEGIN postrow -->\n	<!-- IF postrow.S_FIRST_UNREAD --><a id="unread"></a><!-- ENDIF -->\n	<div id="p{postrow.POST_ID}" class="post <!-- IF postrow.S_ROW_COUNT is odd -->bg1<!-- ELSE -->bg2<!-- ENDIF --><!-- IF postrow.S_UNREAD_POST --> unreadpost<!-- ENDIF --><!-- IF postrow.S_POST_REPORTED --> reported<!-- ENDIF --><!-- IF postrow.S_ONLINE and not postrow.S_IGNORE_POST --> online<!-- ENDIF -->">\n		<div class="inner"><span class="corners-top"><span></span></span>\n\n		<div class="postbody" id="post{postrow.POST_ID}">\n			<!-- IF postrow.S_IGNORE_POST -->\n				<div class="ignore">{postrow.L_IGNORE_POST}</div>\n			<!-- ELSE -->\n\n		<!-- IF not S_IS_BOT -->\n			<!-- IF postrow.U_EDIT or postrow.U_DELETE or postrow.U_REPORT or postrow.U_WARN or postrow.U_INFO or postrow.U_QUOTE -->\n				<ul class="profile-icons">\n				  <!-- IF postrow.S_UNREAD_POST --><li style="color:red;font-weight:bold">Nieczytany post</li><!-- ENDIF --> \n          <!-- IF postrow.U_EDIT --><li class="qe-icon"><a href="#" onclick="return false;" id="quickedit{postrow.POST_ID}" title="{L_QUICKEDIT_POST}"><span>{L_QUICKEDIT_POST}</span></a></li><!-- ENDIF -->\n					<!-- IF postrow.U_EDIT --><li class="edit-icon"><a href="{postrow.U_EDIT}" title="{L_EDIT_POST}"><span>{L_EDIT_POST}</span></a></li><!-- ENDIF -->\n          <!-- IF postrow.U_DELETE --><li class="delete-icon"><a href="{postrow.U_DELETE}" title="{L_DELETE_POST}"><span>{L_DELETE_POST}</span></a></li><!-- ENDIF -->\n					<!-- IF postrow.U_REPORT --><li class="report-icon"><a href="{postrow.U_REPORT}" title="{L_REPORT_POST}"><span>{L_REPORT_POST}</span></a></li><!-- ENDIF -->\n					<!-- IF postrow.U_WARN --><li class="warn-icon"><a href="{postrow.U_WARN}" title="{L_WARN_USER}"><span>{L_WARN_USER}</span></a></li><!-- ENDIF -->\n					<!-- IF postrow.U_INFO --><li class="info-icon"><a href="{postrow.U_INFO}" title="{L_INFORMATION}"><span>{L_INFORMATION}</span></a></li><!-- ENDIF -->\n					<!-- IF S_QUICK_REPLY and postrow.DECODED_MESSAGE --><li class="quote-icon"><a href="#postingbox" onclick="addquote({postrow.POST_ID}, \'{postrow.POST_AUTHOR}\');" title="{L_QUOTE} {postrow.POST_AUTHOR}"><span>{L_QUOTE} {postrow.POST_AUTHOR}</span></a></li><!-- ELSEIF postrow.U_QUOTE --><li class="quote-icon"><a href="{postrow.U_QUOTE}" title="{L_REPLY_WITH_QUOTE}"><span>{L_REPLY_WITH_QUOTE}</span></a></li><!-- ENDIF -->\n          \n          <!-- IF postrow.S_FORUM_REPUTATION and S_USER_LOGGED_IN and not postrow.S_IS_OWN_POST and not postrow.S_ALREADY_REPUT -->\n            <li class="thanks-icon"><a href="{postrow.REPUTATION_LINK}" title="{postrow.REPUTATION_ALT}"></a></li>\n          <!-- ELSE -->\n          <!-- ENDIF -->  					\n        </ul>\n			<!-- ENDIF -->\n		<!-- ENDIF -->\n\n			<h3 <!-- IF postrow.S_FIRST_ROW -->class="first"<!-- ENDIF -->><!-- IF postrow.POST_ICON_IMG --><img src="{T_ICONS_PATH}{postrow.POST_ICON_IMG}" width="{postrow.POST_ICON_IMG_WIDTH}" height="{postrow.POST_ICON_IMG_HEIGHT}" alt="" /> <!-- ENDIF --><a href="{U_VIEW_TOPIC}#p{postrow.POST_ID}">{postrow.POST_SUBJECT}</a></h3>\n			<p class="author"><!-- IF S_IS_BOT -->{postrow.MINI_POST_IMG}<!-- ELSE --><a href="{postrow.U_MINI_POST}">{postrow.MINI_POST_IMG}</a><!-- ENDIF -->{L_POST_BY_AUTHOR} <strong>{postrow.POST_AUTHOR_FULL}</strong> &raquo; {postrow.POST_DATE} </p>\n\n			<!-- IF postrow.S_POST_UNAPPROVED or postrow.S_POST_REPORTED -->\n				<p class="rules">\n					<!-- IF postrow.S_POST_UNAPPROVED -->{UNAPPROVED_IMG} <a href="{postrow.U_MCP_APPROVE}"><strong>{L_POST_UNAPPROVED}</strong></a><br /><!-- ENDIF -->\n					<!-- IF postrow.S_POST_REPORTED -->{REPORTED_IMG} <a href="{postrow.U_MCP_REPORT}"><strong>{L_POST_REPORTED}</strong></a><!-- ENDIF -->\n				</p>\n			<!-- ENDIF -->\n\n			<div class="content">{postrow.MESSAGE}</div>\n			<!-- IF postrow.U_QUOTE and postrow.DECODED_MESSAGE -->\n				<div id="message_{postrow.POST_ID}" style="display: none;">{postrow.DECODED_MESSAGE}</div>\n			<!-- ENDIF -->\n\n			<!-- IF postrow.S_HAS_ATTACHMENTS -->\n				<dl class="attachbox">\n					<dt>{L_ATTACHMENTS}</dt>\n					<!-- BEGIN attachment -->\n						<dd>{postrow.attachment.DISPLAY_ATTACHMENT}</dd>\n					<!-- END attachment -->\n				</dl>\n			<!-- ENDIF -->\n\n			<!-- IF postrow.S_DISPLAY_NOTICE --><div class="rules">{L_DOWNLOAD_NOTICE}</div><!-- ENDIF -->\n			<!-- IF postrow.EDITED_MESSAGE or postrow.EDIT_REASON -->\n				<div class="notice">{postrow.EDITED_MESSAGE}\n					<!-- IF postrow.EDIT_REASON --><br /><strong>{L_REASON}:</strong> <em>{postrow.EDIT_REASON}</em><!-- ENDIF -->\n				</div>\n			<!-- ENDIF -->\n\n			<!-- IF postrow.BUMPED_MESSAGE --><div class="notice"><br /><br />{postrow.BUMPED_MESSAGE}</div><!-- ENDIF -->\n			<!-- IF postrow.SIGNATURE --><div id="sig{postrow.POST_ID}" class="signature">{postrow.SIGNATURE}</div><!-- ENDIF -->\n		<!-- ENDIF -->\n\n		</div>\n\n		<!-- IF not postrow.S_IGNORE_POST -->\n			<dl class="postprofile" id="profile{postrow.POST_ID}">\n			<dt>\n				<!-- IF postrow.POSTER_AVATAR -->\n					<!-- IF postrow.U_POST_AUTHOR --><a href="{postrow.U_POST_AUTHOR}">{postrow.POSTER_AVATAR}</a><!-- ELSE -->{postrow.POSTER_AVATAR}<!-- ENDIF --><br />\n				<!-- ENDIF -->\n				<!-- IF not postrow.U_POST_AUTHOR --><strong>{postrow.POST_AUTHOR_FULL}</strong><!-- ELSE -->{postrow.POST_AUTHOR_FULL}<!-- ENDIF -->\n			</dt>\n\n			<!-- IF postrow.RANK_TITLE or postrow.RANK_IMG --><dd>{postrow.RANK_TITLE}<!-- IF postrow.RANK_TITLE and postrow.RANK_IMG --><br /><!-- ENDIF -->{postrow.RANK_IMG}</dd><!-- ENDIF -->\n\n		<dd>&nbsp;</dd>\n\n		<!-- IF postrow.POSTER_POSTS != \'\' --><dd><strong>{L_POSTS}:</strong> {postrow.POSTER_POSTS}</dd><!-- ENDIF -->\n		<!-- IF postrow.POSTER_JOINED --><dd><strong>{L_JOINED}:</strong> {postrow.POSTER_JOINED}</dd><!-- ENDIF -->\n		<!-- IF postrow.POSTER_FROM --><dd><strong>{L_LOCATION}:</strong> {postrow.POSTER_FROM}</dd><!-- ENDIF -->\n		\n		<dd><strong>{L_REPUTATION}:</strong>\n        <a href="{postrow.POSTER_REPUTATION_LINK}" style="color:<!-- IF postrow.POSTER_REPUTATION > 0 -->green<!-- ELSEIF postrow.POSTER_REPUTATION < 0 -->red<!-- ENDIF -->"> {postrow.POSTER_REPUTATION}</a>  \n    </dd>\n\n		<!-- IF postrow.S_PROFILE_FIELD1 -->\n			<!-- Use a construct like this to include admin defined profile fields. Replace FIELD1 with the name of your field. -->\n			<dd><strong>{postrow.PROFILE_FIELD1_NAME}:</strong> {postrow.PROFILE_FIELD1_VALUE}</dd>\n		<!-- ENDIF -->\n\n		<!-- BEGIN custom_fields -->\n			<dd><strong>{postrow.custom_fields.PROFILE_FIELD_NAME}:</strong> {postrow.custom_fields.PROFILE_FIELD_VALUE}</dd>\n		<!-- END custom_fields -->\n\n		<!-- IF not S_IS_BOT -->\n		<!-- IF postrow.U_PM or postrow.U_EMAI