(1, 'fs_periodic_daily.html', '', 1297342680, '<!-- INCLUDE fs_header.html -->\r\n<form id="fs" method="post" action="{S_FS_ACTION}"{S_FORM_ENCTYPE}>\r\n<h2>{L_TITLE}</h2>\r\n\r\n<div class="panel">\r\n	<div class="inner_fs"><span class="corners-top"><span></span></span>\r\n	\r\n	<div class="user_prompt"><span><i>{STATS_MONTH_EXPLAIN}</i></span></div>\r\n	<h3>{L_OVERALL}</h3>\r\n	<table class="fs_details_list">\r\n		<tr>\r\n			<td class="col1">{L_TOTAL_TOPICS}:</td> <td class="col2">{TOTAL_TOPICS}</td>\r\n			<td class="col3">{L_AVG_TOPICS_DAY}:</td> <td class="col4">{AVG_TOPICS}</td>\r\n		</tr>\r\n		<tr>\r\n			<td class="col1">{L_TOTAL_POSTS}:</td> <td class="col2">{TOTAL_POSTS}</td>			\r\n			<td class="col3">{L_AVG_POSTS_DAY}:</td> <td class="col4">{AVG_POSTS}</td>\r\n		</tr>\r\n		<tr>\r\n			<td class="col1">{L_TOTAL_USER_REGS}:</td> <td class="col2">{TOTAL_USER_REGS}</td>\r\n			<td class="col3">{L_AVG_USER_REGS_DAY}:</td> <td class="col4">{AVG_USER_REGS}</td>\r\n		</tr>\r\n	</table><br />	\r\n	<h3>{L_TOPICS}</h3>\r\n	<!-- IF S_DAILY_TOPICS -->\r\n	<table class="fs_details_graphs">\r\n		<colgroup span="4">\r\n			<col class="col_cat" />\r\n			<col class="col_bar" />\r\n			<col class="col_count" />\r\n			<col class="col_pct" />\r\n		</colgroup>\r\n		<thead>\r\n			<td>{L_PERIODIC_DAY}</td>\r\n			<td></td>\r\n			<td>{L_COUNT}</td>\r\n			<td>{L_PERCENT}</td>\r\n		</thead>\r\n		<tbody>\r\n		<!-- BEGIN periodic_topics_row -->\r\n			<tr<!-- IF periodic_topics_row.IS_MAX --> style="font-weight:bold"<!-- ENDIF -->>\r\n				<td>{periodic_topics_row.TIME_ELEMENT}</td><td><div class="graph" style="width:{periodic_topics_row.BARWIDTH}%;">&nbsp;</div></td><td>{periodic_topics_row.COUNT}</td><td>{periodic_topics_row.PCT}%</td>\r\n			</tr>\r\n		<!-- END periodic_topics_row -->\r\n		</tbody>	\r\n	</table><br />\r\n	<!-- ELSE -->{L_NONE}<br /><br />\r\n	<!-- ENDIF -->\r\n	<h3>{L_POSTS}</h3>\r\n	<!-- IF S_DAILY_POSTS -->\r\n	<table class="fs_details_graphs">\r\n		<colgroup span="4">\r\